<script>
import IconSex from "../icon/icon-sex.vue";
import IconEyeCatching from "../icon/icon-eye-catching.vue";
import PageTitleArticle from "../organism/page-title-article.vue";
import axios from "axios";

export default {
  name: "PageArticle",
  props: {},
  components: {
    PageTitleArticle,
    IconSex,
    IconEyeCatching
  },
  watch: {
    $route: function(to, from) {
      global.$(".o-answering-form").css("display", "block");
      this.fetchArticles();
    }
  },
  created: function() {
    if(!localStorage.getItem('history-vote')){
      localStorage.setItem('history-vote',"[]")
    }else{
      this.historyVote = JSON.parse(localStorage.getItem('history-vote'));
    }
    this.fetchArticles();
  },
  computed: {
    isFullOpt1: function() {
      if (this.calcOpt1Amount() >= this.calcOpt2Amount()) {
        return true;
      } else {
        return false;
      }
    },
    isVoted: function(){
      let target = this.historyVote.find((h) => {return (h.post_id == this.$route.params.id)});
      return (target == undefined)
    }
  },
  data: function() {
    return {
      article: null,
      vote: {
        age: "",
        sex: "",
        selected_opt: ""
      },
      historyVote: [],
      editors_pick: null,
      latest: null,
      voteRate: 0,
      title: null,
      description: null,
      selectedOpt1Class: 'is-option1',
      selectedOpt2Class: 'is-option2'
    };
  },
  mounted: function() {
    global.$("body").addClass("p-article");
  },
  destroyed: function() {
    global.$("body").removeClass("p-article");
  },
  methods: {
    opt1_amount: function() {
      let amount = 0;
      if (this.vote.selected_opt == "opt1") {
        amount = this.article.votes.opt1_selected.amount + 1;
      } else {
        amount = this.article.votes.opt1_selected.amount;
      }
      return amount;
    },
    opt2_amount: function() {
      let amount = 0;
      if (this.vote.selected_opt == "opt2") {
        amount = this.article.votes.opt1_selected.amount + 1;
      } else {
        amount = this.article.votes.opt1_selected.amount;
      }
      return amount;
    },
    fetchArticles: function() {
      this.$store.commit("setLoading", true);
      if (this.$route.params) {
        let url =
          `${ this.API_URL }/api/v1/articles/`;
        url += this.$route.params.id;
        if (this.$store.state.latest && this.$store.state.editors_pick) {
          this.latest = this.$store.state.latest;
          this.editors_pick = this.$store.state.editors_pick;
          axios
            .get(url)
            .then(result => {
              this.article = result.data.article;
              this.setMetaTag(result.data.article.post);
            })
            .finally(() => {
              this.$emit("updateHead");
              this.$store.commit("setLoading", false);
              this.$nextTick(() => this.format_answering_area());
            });
        } else {
          axios
            .all([
              axios.get(url),
              axios.get(
                `${ this.API_URL }/api/v1/latest?limit=3`
              ),
              axios.get(
                `${ this.API_URL }/api/v1/editors_picks?limit=6`
              )
            ])
            .then(
              axios.spread((api1Result, api2Result, api3Result) => {
                this.article = api1Result.data.article;
                this.latest = api2Result.data.articles;
                this.editors_pick = api3Result.data.articles;
                this.setMetaTag(api1Result.data.article.post);
              })
            )
            .finally(() => {
              this.$emit("updateHead");
              this.$store.commit("setLoading", false);
              this.$nextTick(() => {
                this.format_answering_area();
                global.$('.o-answering-form').css('visibility','visible')
                });
            });
        }
      }
    },
    answer_opt: function(selected_opt, e) {
      this.vote.selected_opt = selected_opt;
      this.toNext(e.currentTarget);
    },
    answer_sex: function(selected_sex, e) {
      this.vote.sex = selected_sex;
      this.toNext(e.currentTarget);
    },
    answer_age: function(selected_age, e) {
      this.vote.age = selected_age;
      this.toNext(e.currentTarget);
    },
    answer_age_ealry_or_late: function(selected, e) {
      this.vote.age = selected + this.vote.age;
      this.toNext(e.currentTarget);
      this.vote_to();
      this.voteRate = this.calcVoteRate(
        this.article.votes.opt2_selected.amount,
        this.article.votes.opt1_selected.amount
      );
    },
    toNext: function(target) {
      global
        .$(target)
        .parents(".o-answering-form")
        // .css("display", "none");
        .fadeOut(150, 'linear');
        this.format_answering_area();
        global.$('.m-chart-area').css('visibility','visible')
    },
    calcVoteRate: function(opt2_amount, opt1_amount) {
      let op1 = opt1_amount;
      let op2 = opt2_amount;
      if (this.vote.selected_opt == "opt1") op1 += 1;
      if (this.vote.selected_opt == "opt2") op2 += 1;
      if (op2 < op1) {
        return (op2 / op1) * 100;
      } else {
        return (op1 / op2) * 100;
      }
    },
    calcOpt1Amount: function() {
      let amount = this.article.votes.opt1_selected.amount;
      if (this.vote.selected_opt == "opt1") amount += 1;
      return amount;
    },
    calcOpt2Amount: function() {
      let amount = this.article.votes.opt2_selected.amount;
      if (this.vote.selected_opt == "opt2") amount += 1;
      return amount;
    },
    vote_to: function() {
      let saveData = {
        post_id: this.$route.params.id,
        age: this.vote.age,
        sex: this.vote.sex,
        selected_opt: this.vote.selected_opt,
        commented: false
      }
      let url =
        `${ this.API_URL }/api/v1/articles/` +
        this.$route.params.id +
        "/vote";
      axios
        .post(url, {
          vote: {
            age: this.vote.age,
            sex: this.vote.sex,
            selected_opt: this.vote.selected_opt
          }
        })
        // eslint-disable-next-line
        .then(response => {
          if(response.status === 201){
            this.historyVote.push(saveData)
            let saveDataString = JSON.stringify(this.historyVote)
            localStorage.setItem('history-vote', saveDataString);
          }
        })
        // eslint-disable-next-line
        .catch(error => {});
    },
    format_answering_area: function() {
      let maxHeight = 0;
      global.$(".o-answering-form").each(function() {
        if (maxHeight > global.$(this).height()) return;
        maxHeight = global.$(this).height();
      });
      global.$(".m-chart-area").each(function() {
        if (maxHeight > global.$(this).height()) return;
        maxHeight = global.$(this).height();
      });
      global.$(".o-answering-form").each(function() {
        global.$(this).height(maxHeight);
      });
      global.$(".m-chart-area").each(function() {
        global.$(this).height(maxHeight);
      });

      global.$('.o-answering-form__inner').css('visibility','visible')
      // global.$('.p-article-answer').css('visibility','visible')
    },
    getTwitterShareUrl: function() {
      return `https://twitter.com/share?text=${this.article.post.title}&url=${
        location.href
      }`;
    },
    getFBShareUrl: function() {
      return `http://www.facebook.com/share.php?u=${location.href}`;
    },
    getLineShareUrl: function() {
      return `https://line.me/R/msg/text/?${this.article.post.title +
        location.href}`;
    },
    scrollToAns: function() {
      let position = $("div.p-article-answer").offset().top;
      $("html,body").animate({ scrollTop: position }, { queue: false });
    },
    setMetaTag: function(post) {
      this.title = post.title;
      this.description = post.content;
    },
    getHistory: function(){
      let target = this.historyVote.find((h) => {return (h.post_id == this.$route.params.id)});
      return target
    }
  },
  head: {
    title: function() {
      return {
        inner: this.title,
        separator: "|",
        complement: "Loverage"
      };
    },
    meta: function() {
      return [
        { property: "", content: this.title + " |Loverage" },
        { name: "description", content: this.description },
        { property: "og:url", content: location.href }
      ];
    }
  }
};
</script>

