<script>
import IconSex from "../icon/icon-sex.vue";
import IconCategory from "../icon/icon-category.vue";
import IconEyeCatching from "../icon/icon-eye-catching.vue";
import PageTitle from "../organism/page-title.vue";
import axios from "axios";

export default {
  name: "PageCategoryDetail",
  props: {},
  watch: {
    $route: function(to, from) {
      this.fetchArticles();
    }
  },
  data: function() {
    return {
      title: "",
      articles: null,
      ranking_view: null,
      latest: null,
      hot_topic: null,
      canGrep: false,
      pageNum: 1,
      articlesGrepped: null,
      grepAgeValue: "",
      grepSexValue: "",
      showCategoryDescription: false,
      categoryDescription: "",
    };
  },
  computed: {
    articleShown: function() {
      if (this.articles) {
        return this.articlesGrepped.length > this.pageNum * 6
          ? this.articlesGrepped.slice(0, this.pageNum * 6 - 1)
          : this.articlesGrepped;
      } else {
        return null;
      }
    },
    maxPages: function() {
      return this.pageNum * 6;
    },
    articlesCount: function() {
      return this.articlesGrepped.length;
    }
  },
  components: {
    PageTitle,
    IconSex,
    IconEyeCatching,
    IconCategory
  },
  created: function() {
    this.setCategoryDiscription()
    this.grepAgeValue = "";
    this.grepSexValue = "";
    this.fetchArticles();
  },
  mounted: function() {
    global.$("body").addClass("p-category-detail");
    this.$emit('updateHead');
  },
  destroyed: function() {
    global.$("body").removeClass("p-category-detail");
  },
  methods: {
    setCategoryDiscription: function(){
      if(this.$route.query.category){
        let category = this.$store.state.categories.filter(c =>{
          return c.name == this.$route.query.category;
        })
        this.categoryDescription = category[0].description
        this.showCategoryDescription = true
      }else{
        this.showCategoryDescription = false
      }
    },
    onGrep: function() {
      let result = null;

      if (this.grepAgeValue !== "" && this.grepSexValue === "") {
        result = this.articles.filter(article => {
          return article.user_age === this.grepAgeValue;
        });
      }
      if (this.grepAgeValue === "" && this.grepSexValue !== "") {
        result = this.articles.filter(article => {
          return article.user_sex === this.grepSexValue;
        });
      }
      if (this.grepAgeValue !== "" && this.grepSexValue !== "") {
        result = this.articles.filter(article => {
          return (
            article.user_sex === this.grepSexValue &&
            article.user_age === this.grepAgeValue
          );
        });
      }
      if (this.grepAgeValue === "" && this.grepSexValue === "") {
        result = this.articles;
      }
      this.articlesGrepped = result;
      this.pageNum = 1;
    },
    ShowMoreArticles: function() {
      this.pageNum += 1;
    },
    fetchArticles: function() {
      this.title = "";
      if (this.$route.query) {
        let url =
          `${ this.API_URL }/api/v1/articles?`;
        if (this.$route.query.sex) {
          if (url.slice(-1) != "?") url += "&";
          url += "sex=" + this.$route.query.sex;
          this.title += `${this.to_jp_sex(this.$route.query.sex)}`;
          this.canGrep = false;
        }
        if (this.$route.query.age) {
          if (url.slice(-1) != "?") url += "&";
          url += "age=" + this.$route.query.age;
          this.title += `${this.to_jp_age(this.$route.query.age)}`;
          this.canGrep = false;
        }
        if (this.$route.query.keyword) {
          if (url.slice(-1) != "?") url += "&";
          url += "keyword=" + this.$route.query.keyword;
          this.title = `"${this.$route.query.keyword}"`;
          this.canGrep = true;
        }
        if (this.$route.query.tag) {
          if (url.slice(-1) != "?") url += "&";
          url += "tag=" + this.$route.query.tag;
          this.title = `タグ "#${this.$route.query.tag}"`;
          this.canGrep = true;
        }
        if (this.$route.query.category) {
          if (url.slice(-1) != "?") url += "&";
          url += "category=" + this.$route.query.category;
          this.title = `カテゴリー "${this.$route.query.category}"`;
          this.canGrep = true;
        }
        this.title += " の検索結果";

        this.$store.commit("setLoading", true);
        if(this.$store.state.ranking_view && this.$store.state.latest && this.$store.state.hot_topic){
          this.ranking_view = this.$store.state.ranking_view
          this.latest = this.$store.state.latest
          this.hot_topic = this.$store.state.hot_topic
          axios
          .get(url)
          .then((result)=>{
            this.articles = result.data.articles
            this.articlesGrepped = this.articles;
          }).finally(() => {
            this.$store.commit("setLoading", false);
          });
        }else{
          axios
            .all([
              axios.get(url),
              axios.get(
                `${ this.API_URL }/api/v1/ranking_view?limit=3`
              ),
              axios.get(
                `${ this.API_URL }/api/v1/latest?limit=3`
              ),
              axios.get(
                `${ this.API_URL }/api/v1/hot_topics?limit=3`
              )
            ])
            .then(
              axios.spread((api1Result, api2Result, api3Result, api4Result) => {
                this.articles = api1Result.data.articles;
                this.articlesGrepped = this.articles;
                this.ranking_view = api2Result.data.articles;
                this.latest = api3Result.data.articles;
                this.hot_topic = api4Result.data.articles;
              })
            )
            .finally(() => {
              this.$store.commit("setLoading", false);
            });
        }
      }
    },
    to_jp_age: function(value) {
      switch (value) {
        case "e_10s":
          return "10代前半";
        case "l_10s":
          return "10代後半";
        case "e_20s":
          return "20代前半";
        case "l_20s":
          return "20代後半";
        case "e_30s":
          return "30代前半";
        case "l_30s":
          return "30代後半";
        case "e_40s":
          return "40代前半";
        case "l_40s":
          return "40代後半";
        case "e_50s":
          return "50代前半";
        case "l_50s":
          return "50代後半";
        case "e_60s":
          return "60代前半";
        case "l_60s":
          return "60代後半";
        default:
          return value;
      }
    },
    to_jp_sex: function(value) {
      switch (value) {
        case "m":
          return "男性";
        case "f":
          return "女性";
        case "o":
          return "その他";
        default:
          return value;
      }
    }
  },
  head: {
    title: function() {
      return {
        inner: "検索結果",
        separator: "|",
        complement: "Loverage"
      };
    },
    meta: function(){
     return [
        { name: 'description', content: '' },
        { property: 'og:title', content: '検索結果|Loverage' },
        { property: 'og:description', content: '' },
        { property: 'og:type', content: 'website' }
      ]
    }
  }
};
</script>

